/**
 * A person object with name, age and sayName method.
 * @property name - The person's name.
 * @property age - The person's age.
 * @property sayName - A function that alerts the person's name.
 */
declare type Person = {
    name: string;
    age: number;
    sayName: (...params: any[]) => any;
};

